import IncomeSum from "./IncomeSum";
import ExpensesSum from "./ExpensesSum";
import "./Header.css";
import { date } from "./Date";
import { GlobalProvider } from "../context/GlobalState";

const Header = (props) => {
  const totalSum = props.sumIncome + props.sumExpense;

  return (
    <div className="header">
      <div className="header__container">
        <p>Available budget in {date}:</p>
        <GlobalProvider>
          {totalSum > 0 ? <h1>+{totalSum}</h1> : <h1>{totalSum}</h1>}
        </GlobalProvider>

        <IncomeSum sumIncome={props.sumIncome} />
        <ExpensesSum sumExpense={props.sumExpense} />
      </div>
    </div>
  );
};

export default Header;
