import { useState } from "react";
import Header from "./components/Header";
import Form from "./components/Form";
import TotalList from "./components/List/TotalList";
import IncomeList from "./components/List/IncomeList";

function App(props) {
  const allData = [];
  const [data, setData] = useState(allData);
  const saveEnteredData = (enteredData) => {
    setData((prevExpenses) => {
      return [enteredData, ...prevExpenses];
    });
  };
  let plus = 0;
  data.map((item) => {
    if (item.sign === "+") {
      plus += item.value;
    }
  });
  let minus = 0;
  data.map((item) => {
    if (item.sign === "-") {
      minus -= item.value;
    }
  });

  return (
    <div>
      <Header sumIncome={plus} sumExpense={minus} />
      <Form onSaveEnteredData={saveEnteredData} />
      <TotalList items={data} />
    </div>
  );
}

export default App;
