import "./Item.css";

const expenseItem = (props) => {
  if (props.sign === "-") {
    return (
      <li className="item">
        <p className="item__description">{props.title}</p>
        <p className="item__value">
          {props.sign}
          {props.amount}
        </p>
      </li>
    );
  }
};

export default expenseItem;
