import { useState } from "react";
import "./Form.css";

const Form = () => {
  const [enteredSign, setEnteredSign] = useState("");
  const [enteredDesc, setEnteredDesc] = useState("");
  const [enteredValue, setEnteredValue] = useState("");

  const signChangeHandler = (event) => {
    setEnteredSign(event.target.value);
  };

  const descChangeHandler = (event) => {
    setEnteredDesc(event.target.value);
  };

  const valueChangeHandler = (event) => {
    setEnteredValue(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: +enteredAmount,
    };
    props.onSaveExpenseData(expenseData);
    setEnteredTitle("");
    setEnteredAmount("");
    setEnteredDate("");
  };

  return (
    <form className="form" onSubmit={submitHandler}>
      <select className="form__select" onChange={signChangeHandler}>
        <option>+</option>
        <option>-</option>
      </select>
      <input
        className="form__input form__input--desc"
        placeholder="Add description"
        maxLength="50"
        onChange={descChangeHandler}
      ></input>
      <input
        className="form__input form__input--value"
        placeholder="Value"
        type="number"
        onChange={valueChangeHandler}
      ></input>
      <button className="form__button" type="submit">
        <i class="fa fa-solid fa-check fa-1x"></i>
      </button>
    </form>
  );
};

export default Form;
