import { useState } from "react";
import Header from "./components/Header";
import Form from "./components/Form";
import TotalList from "./components/List/TotalList";
import UserContext from "./context/GlobalState";

function App(props) {
  const [data, setData] = useState([]);
  const saveEnteredData = (enteredData) => {
    setData((prevExpenses) => {
      return [enteredData, ...prevExpenses];
    });
  };

  let plus = 0;
  data.forEach((item) => {
    if (item.sign === "+") {
      plus += item.value;
    }
  });
  let minus = 0;
  data.forEach((item) => {
    if (item.sign === "-") {
      minus -= item.value;
    }
  });

  const chosenId = (event) => {
    const id = event;

    for (var i = 0; i < data.length; i++) {
      if (data[i].id === id) {
        data.splice(i, 1);
        i--;
      }
      setData(data);
    }
    // setData((id) => {
    //   for (var i = 0; i < data.length; i++) {
    //     if (data[i].id === event) {
    //       data.splice(i, 1);
    //       i--;
    //     }
    //   }
    // });
  };

  return (
    <div>
      <Header sumIncome={plus} sumExpense={minus} />
      <Form onSaveEnteredData={saveEnteredData} />
      <UserContext.Provider value={data}>
        <TotalList items={data} chosenId={chosenId} />
      </UserContext.Provider>
    </div>
  );
}

export default App;
