import "./Item.css";

const Item = (props) => {
  const typedSign = "";
  if (props.sign === "-") {
    typedSign = "-";
  } else {
    typedSign = "+";
  }
  return (
    <li className="item">
      <p className="item__description">{props.title}</p>
      <p className="item__value">
        {typedSign}
        {props.amount}
      </p>
    </li>
  );
};

export default Item;
