import "./IncomeSum.css";

const IncomeSum = (props) => {
  const incomes = [];
  props.income.map((item) => {
    if (item.sign === "+") {
      incomes = item.value;
    }
  });
  const totalIncome = incomes.reduce((acc, item) => (acc += item), 0);

  return (
    <div className="income-sum">
      <p>Income</p>
      <p>{totalIncome}</p>
    </div>
  );
};

export default IncomeSum;
